import {
  IonButtons,
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardTitle,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonMenuButton,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
  useIonViewWillEnter,
} from "@ionic/react";
import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import Connection from "../../mixins/Connection";

interface ResponseObject{
  name: string,
  value: number
}

const Dashboard: React.FC = () => {

useIonViewWillEnter(()=>{
    getPast7Days();
});

let past7Days: ResponseObject[];
let values: number[];

const [showLoader, setShowLoader] = useState(true);
const [showError, setShowError] = useState(false);
const [errorMessage, setErrorMessage] = useState("");

const getPast7Days = () => {
  let url = '/query/past7Days'
  Connection.processGetRequest({}, url, (response: any)=>{
    mapPast7Days(response);
  })
};

const state = {
  labels: ["Today", "Yesterday" , "2 Days Ago", "3 Days Ago", "4 Days Ago", "5 Days Ago", "6 Days Ago", "7 Days Ago"],
  datasets: [
    {
      label: "Bookings",
      backgroundColor: "rgba(75,192,192,1)",
      borderColor: "rgba(0,0,0,1)",
      borderWidth: 2,
      data: [],
    },
  ],
};

const mapPast7Days = (response: any) => {
  if (response.type === "error") {
    setShowLoader(false);
    setErrorMessage(response.data);
    setShowError(true);
  } else {
    setShowLoader(false);
    var num2 = 9;
    past7Days = response.data.data;
    console.log(past7Days[0].value);
  }
};

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>Dashboard</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen>
        <IonGrid>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonCardTitle color="grey">Bookings for the past 7 days</IonCardTitle>
                  <IonCardContent>
                    <Bar
                      data={state}
                    />
                  </IonCardContent>
                </IonCardHeader>
              </IonCard>
            </IonCol>
          </IonRow>
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Dashboard;
