/** @jsxImportSource theme-ui */
import { jsx } from "theme-ui";
import {
  IonAlert,
  IonBackButton,
  IonButton,
  IonButtons,
  IonCard,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonImg,
  IonInput,
  IonItem,
  IonLabel,
  IonLoading,
  IonPage,
  IonRow,
  IonSelect,
  IonSelectOption,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";
import { validCellNum } from "../../components/Regex/Regex";
import TGWLogo from '../../components/TGWLogo.png';
import Connection from "../../mixins/Connection";

const ForgotPassword: React.FC = () => {
  const path = useHistory();

  const [cellNum, setCellNum] = useState("");
  const [email, setEmail] = useState("");
  const [selection, setSelection] = useState('Cell Number');
  const [errorMessage, setErrorMessage] = useState("");
  const [showError, setShowError] = useState(false);
  const [showLoader, setShowLoader] = useState(false);

  const updateCellNum = (cellNum: any) => setCellNum(cellNum);
  const updateEmail = (email: any) => setEmail(email);
  const updateSelection = (selection: any) => {
    setSelection(selection.target.value);
  }

  const buttonHandler = () => {
      setShowLoader(true);
    if (cellNum !== "") {
      if (validCellNum.test(cellNum)) {
        let url = "forgot/verify/".concat(cellNum);
        setShowLoader(false);
        path.replace('/forgot/otp');
        // Connection.processPostRequest({}, url, (response: any) =>
        //   mapResponse(response)
        // );
      } else {
        setShowLoader(false);
        setErrorMessage("Invalid Cell Number.");
        setShowError(true);
      }
    } else {
      setShowLoader(false);
      setErrorMessage("You need to provide a Cell Number.");
      setShowError(true);
    }
  };

  const mapResponse = (response: any) => {
    if (response.type === "error") {
      setShowLoader(false);
      setErrorMessage(response.data.data);
      setShowError(true);
    } else {
      path.push("/otp");
    }
  };

  return (
    <IonPage>
      <IonLoading
        cssClass="my-custom-class"
        showBackdrop
        isOpen={showLoader}
        message={"Please wait..."}
      />

      <IonAlert
        isOpen={showError}
        onDidDismiss={() => setShowError(false)}
        header={"Error"}
        subHeader={"Something went wrong."}
        message={errorMessage}
        buttons={["OK"]}
      />
      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonBackButton defaultHref="/signIn" />
          </IonButtons>
          <IonTitle>Forgot Password</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonCard sx={styles.card}>
          <IonGrid>
            <IonRow>
              <IonCol>
              <IonItem>
            <IonLabel>Gender</IonLabel>
            <IonSelect value={selection} placeholder="Select One" onIonChange={e => updateSelection(e.detail.value)}>
              <IonSelectOption value="Cell Number">Cell Number</IonSelectOption>
              <IonSelectOption value="Email">Email</IonSelectOption>
            </IonSelect>
          </IonItem>
              </IonCol>
            </IonRow>
            <IonRow>
              <IonCol>
                <IonToolbar>
                  <IonTitle size="small">
                    Please enter the cell number associated with your account
                  </IonTitle>
                </IonToolbar>
              </IonCol>
            </IonRow>
            <IonRow>
              {selection === 'Cell Number' ?
              <IonCol>
                <IonItem sx={styles.input}>
                  <IonLabel position="floating">Cell Number</IonLabel>
                  <IonInput type="text" onIonChange={(e) => updateCellNum(e.detail.value)} />
                </IonItem>
              </IonCol> :
              <IonCol>
              <IonItem sx={styles.input}>
                <IonLabel position="floating">Email</IonLabel>
                <IonInput type="text" onIonChange={(e) => updateEmail(e.detail.value)} />
              </IonItem>
            </IonCol>}
            </IonRow>
            <IonRow>
              <IonCol>
                <IonButton
                  shape="round"
                  type="submit"
                  expand="block"
                  onClick={buttonHandler}
                >
                  Submit
                </IonButton>
              </IonCol>
            </IonRow>
          </IonGrid>
        </IonCard>
      </IonContent>
    </IonPage>
  );
};

export default ForgotPassword;

const styles = {
  input: {
    mb: 2,
  },
  card:{
      mt: '30vh',
  },
};
