import {
  IonButtons,
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardTitle,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonMenuButton,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
  useIonViewWillEnter,
} from "@ionic/react";
import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import Connection from "../../mixins/Connection";

useIonViewWillEnter(()=>{
  getPast7Days();
})

const [past7Days, setPast7Days] = useState({});

const [showLoader, setShowLoader] = useState(true);
const [showError, setShowError] = useState(false);
const [errorMessage, setErrorMessage] = useState("");

const state = {
  labels: ["Today", "Yesterday" , "2 Days Ago", "3 Days Ago", "4 Days Ago", "5 Days Ago", "6 Days Ago", "7 Days Ago"],
  datasets: [
    {
      label: "Bookings",
      backgroundColor: "rgba(75,192,192,1)",
      borderColor: "rgba(0,0,0,1)",
      borderWidth: 2,
      data: [65, 59, 80, 81, 56],
    },
  ],
};

const getPast7Days = () => {
  let url = '/query/past7Days'
  Connection.processGetRequest({}, url, (response: any)=>{
    mapPast7Days(response);
  })
};

const mapPast7Days = (response: any) => {
  if (response.type === "error") {
    setShowLoader(false);
    setErrorMessage(response.data);
    setShowError(true);
  } else {
    setShowLoader(false);
    setPast7Days(response.data.data);
    console.log(past7Days);
  }
};

const Dashboard: React.FC = () => {
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>Dashboard</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen>
        <IonGrid>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonCardTitle>Bookings for the past 7 days</IonCardTitle>
                  <IonCardContent>
                    <Bar
                      data={state}
                    />
                  </IonCardContent>
                </IonCardHeader>
              </IonCard>
            </IonCol>
          </IonRow>
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Dashboard;
