import {
  IonAlert,
  IonButtons,
  IonContent,
  IonFab,
  IonFabButton,
  IonHeader,
  IonIcon,
  IonLoading,
  IonMenuButton,
  IonPage,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import { add, card } from "ionicons/icons";
import { useState } from "react";
import { useParams } from "react-router";
import Card from "../../components/Card/Card";
import Connection from "../../mixins/Connection";
import "./Page.css";

const Management: React.FC = () => {
  let cards;

  const addUrl = "/add/";

  const { name, title } =
    useParams<{
      name: string;
      title: "agent" | "complex" | "package" | "promotion";
    }>();

  const [showLoader, setShowLoader] = useState(false);
  const [showError, setShowError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [agents, setAgents] = useState([
    {
      agentId: "",
      complexName: "",
      contractId: "",
      name: "",
      surname: "",
      id: "",
      cellNum: "",
      street1: "",
      street2: "",
      city: "",
      province: "",
      postalCode: "",
      password: "",
    },
  ]);
  const [complexes, setComplexes] = useState([
    {
      complexId: "",
      name: "",
      street1: "",
      street2: "",
      city: "",
      province: "",
      postalCode: "",
      telephoneNum: "",
      startTime: "",
      endTime: "",
      cellNum: "",
      units: [],
      agents: [],
    },
  ]);
  const [packages, setPackages] = useState([
    {
      packageId: "",
      promotionId: "",
      packageName: "",
      minutes: 0,
      standardPrice: "",
      suvPrice: "",
      description: "",
      onPromotion: false,
    },
  ]);
  const [promotions, setPromotions] = useState([
    {
      promotionId: "",
      promotionName: "",
      packageName: "",
      standardPrice: "",
      suvPrice: "",
      isEnabled: false,
    },
  ]);

  const getAgents = () => {
    var url = "agent/getAll";
    Connection.processGetRequest({}, url, (response: any) => {
      mapAgents(response);
    });
  };
  const getComplexes = () => {
    var url = "complex/getAll";
    Connection.processGetRequest({}, url, (response: any) => {
      mapComplexes(response);
    });
  };
  const getPackages = () => {
    var url = "package/getAll";

    Connection.processGetRequest({}, url, (response: any) => {
      mapPackages(response);
    });
  };
  const getPromotions = () => {
    var url = "promotion/getAll";
    Connection.processGetRequest({}, url, (response: any) => {
      mapPromotions(response);
    });
  };

  const mapPackages = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      setPackages(response.data.data);
    }
  };
  const mapAgents = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      setAgents(response.data.data);
    }
  };

  const mapComplexes = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      setComplexes(response.data.data);
    }
  };
  const mapPromotions = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      setPromotions(response.data.data);
    }
  };

  if (title === "agent") {
    getAgents();
    if (agents.length === 0) {
      cards = agents.map((agent) => (
        <Card
          type={title}
          id={agent.agentId}
          key={agent.agentId}
          title={agent.name.concat(" ", agent.surname)}
          subtitle={agent.complexName}
          reference={name}
        />
      ));
    } else {
      cards = <h2>No Agents</h2>;
    }
  } else if (title === "complex") {
    getComplexes();
    if (complexes.length !== 0) {
      cards = complexes.map((complex) => (
        <Card
          type={title}
          id={complex.complexId}
          key={complex.complexId}
          title={complex.name}
          subtitle={complex.street2}
          reference={name}
        />
      ));
    } else {
      cards = <h2>No Complexes</h2>;
    }
  } else if (title === "package") {
    getPackages();
    if(packages.length !== 0){
    cards = packages.map((pack) => (
      <Card
        type={title}
        id={pack.packageId}
        key={pack.packageId}
        title={pack.packageName}
        subtitle={pack.description}
        reference={name}
      />
    ));
    } else {
      cards = <h2>No Packages</h2>;
    }
  } else {
    getPromotions();
    if (promotions.length !== 0) {
      cards = promotions.map((promotion) => (
        <Card
          type={title}
          id={promotion.promotionId}
          key={promotion.promotionId}
          title={promotion.promotionName}
          subtitle={promotion.packageName}
          reference={name}
        />
      ));
    } else {
      cards = <h2>No Promotions</h2>;
    }
  }

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>{name.concat(" Management")}</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen>
      <IonLoading
            cssClass='my-custom-class'
            showBackdrop
            isOpen={showLoader}
            message={'Please wait...'}
          />

          <IonAlert
            isOpen={showError}
            onDidDismiss={() => setShowError(false)}
            header={'Error'}
            subHeader={'Something went wrong.'}
            message={errorMessage}
            buttons={['OK']}
          />
        {cards}
        <IonFab vertical="bottom" horizontal="end" slot="fixed">
          <IonFabButton href={addUrl.concat(name, "/", title)}>
            <IonIcon icon={add} />
          </IonFabButton>
        </IonFab>
      </IonContent>
    </IonPage>
  );
};

export default Management;
