import {
  IonButton,
  IonChip,
  IonCol,
  IonDatetime,
  IonGrid,
  IonIcon,
  IonInput,
  IonItem,
  IonItemDivider,
  IonItemGroup,
  IonLabel,
  IonRow,
} from "@ionic/react";
import { add, pin, remove, trash } from "ionicons/icons";
import React, { ReactComponentElement, useState } from "react";
import Connection from "../../mixins/Connection";

const ComplexForm: React.FC<{
  name: string;
  isDisabled: boolean;
  content: any;
}> = (props) => {
  const {
    name,
    stree1,
    street2,
    city,
    province,
    postalCode,
    telephoneNum,
    startTime,
    endTime,
    cellNum,
    agents,
    units,
  } = props.content;

  const [unitList, setUnitList] = useState<string[]>(units);
  const [isUnit, setIsUnit] = useState(true);

  const updateUnitList = (unitList: any) => setUnitList(unitList);

  const addUnit = (e: any, f: any) => {
      e.preventDefault();
    if (f == "Enter" && e.target.value !== '') {
      unitList.push(e.target.value);
      setUnitList(unitList);
      console.log(unitList);
      e.target.value = '';
    }
  };

  const removeUnit = (e: any, unit: any) => {
    e.preventDefault();
    let temp:any = [];
      for(let i = 0; i < unitList.length; i++){
          if(unitList[i] !== unit){
              temp.push(unitList[i]);
          }
      }
      setUnitList(temp);
      console.log(temp);
  };


  const submitHandler = () => {};

  return (
    <IonGrid>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Complex Name</IonLabel>
            <IonInput value={name}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Street 1</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Street 2</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>City</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Province</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel>Postal code</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Telephone Number</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Operational Start Time</IonLabel>
            <IonDatetime displayFormat="+HH:mm" />
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel>Operational End Time</IonLabel>
            <IonDatetime displayFormat="+HH:mm" />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Cell Number</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItemDivider>
            <IonLabel>Units</IonLabel>
          </IonItemDivider>
          {unitList !== [''] && unitList.map((unit) => (
            <IonChip key={unit}>
              <IonLabel>{unit}</IonLabel>
              <IonIcon icon={trash} onClick={(e)=>removeUnit(e,unit)}/>
            </IonChip>
          ))}
          <IonItem>
            <IonInput onKeyUp={(e) => addUnit(e, e.key)} />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton type="submit" expand="block" onClick={submitHandler}>
            {props.name}
          </IonButton>
        </IonCol>
      </IonRow>
    </IonGrid>
  );
};

export default ComplexForm;
