  IonCol,
  IonGrid,
  IonInput,
  IonItem,
  IonLabel,
  IonRow,
  IonTextarea,
  IonToggle,
} from "@ionic/react";
import React, { useState } from "react";
import Connection from "../../mixins/Connection";

interface PackageObject
  {
    packageId: string,
    promotionId: string,
    packageName: string,
    minutes: number,
    standardPrice: string,
    suvPrice: string,
    description: string,
    onPromotion: boolean,
  }

const PackageForm: React.FC<{ name: string; isDisabled: boolean; content: any}> = (props) => {


