  const [ showError, setShowError ] = useState(false);
  const [ errorMessage, setErrorMessage ] = useState('');

  const [enteredPackageName, setEnteredPackageName] = useState(props.content.packageName);
  const [enteredMinutes, setEnteredMinutes] = useState(props.content.minutes);
  const [enteredStandardPrice, setEnteredStandardPrice] = useState(props.content.standardPrice);
  const [enteredSuvPrice, setEnteredSuvPrice] = useState(props.content.suvPrice);
  const [enteredDescription, setEnteredDescription] = useState(props.content.description);
  const [enteredOnPromotion, setOnPromotion] = useState(props.content.onPromotion);

  // const buttonHandler = () =>{
  //   if(props.name === "Update"){
  //     var url = "package/update";
