  //       mapResponse(response);
  //     })
  //   }
  // }

  // const mapResponse = (response: any) => {

  // };
  return (
    <IonGrid>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Package Name</IonLabel>
            <IonInput disabled={props.isDisabled} value={enteredPackageName}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Minutes</IonLabel>
            <IonInput disabled={props.isDisabled} value={enteredMinutes}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Standard Price</IonLabel>
            <IonInput disabled={props.isDisabled} value={enteredStandardPrice}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">SUV Price</IonLabel>
            <IonInput disabled={props.isDisabled} value={enteredSuvPrice}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Description</IonLabel>
            <IonTextarea disabled={props.isDisabled} value={enteredDescription}/>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>On Promotion</IonLabel>
            <IonToggle disabled={props.isDisabled} checked={enteredOnPromotion}/>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton type="submit" expand="block">
            {props.name}
