/** @jsxImportSource theme-ui */
import { Storage } from "@capacitor/storage";
import { IonAlert, IonBackButton, IonButton, IonButtons, IonCard, IonCardContent, IonCardHeader, IonCardSubtitle, IonCardTitle, IonCol, IonContent, IonGrid, IonHeader, IonInput, IonItem, IonLabel, IonLoading, IonPage, IonRow, IonTitle, IonToolbar } from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";
import { validOtp } from "../../components/Regex/Regex";
import Connection from "../../mixins/Connection";

const Otp: React.FC = () => {
    const path = useHistory(); 
  const [otp, setOtp] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [showError, setShowError] = useState(false);
  const [showLoader, setShowLoader] = useState(false);

  const updateOtp = (otp: any) => setOtp(otp.target.value);

  const toIsoString = (date: Date) => {
    var tzo = -date.getTimezoneOffset(),
        dif = tzo >= 0 ? '+' : '-',
        pad = function(num: number) {
            var norm = Math.floor(Math.abs(num));
            return (norm < 10 ? '0' : '') + norm;
        };

    return date.getFullYear() +
        '-' + pad(date.getMonth() + 1) +
        '-' + pad(date.getDate()) +
        'T' + pad(date.getHours()) +
        ':' + pad(date.getMinutes()) +
        ':' + pad(date.getSeconds()) +
        dif + pad(tzo / 60) +
        ':' + pad(tzo % 60);
  }

  const buttonHandler = () => {
      if(otp === ''){
          setShowLoader(false);
          setErrorMessage("Please enter One-Time Pin.");
          setShowError(true);
      }else if(!validOtp.test(otp)){
          setShowLoader(false);
          setErrorMessage("Invalid One-Time Pin.");
          setShowError(true);
      }else{
        let date = new Date();
        let time = toIsoString(date)
        let url = '/forgot/verifyOtp/'.concat(otp, '/', time)
          Connection.processPostRequest({}, url, (response: any) => mapResponse(response))
      }
  };

  const mapResponse = (response: any) => {
    if(response.type === 'error'){
      setShowLoader(false);
      setErrorMessage(response.data.data);
      setShowError(true);
    }else{
      const setAdminId = async () => {
        await Storage.set({
          key: 'adminId',
          value: response.data.data,
        });
      };
      setShowLoader(false);
      path.push('/forgot/newPassword');
    }
  };

  return (
    <IonPage>
      <IonLoading
        cssClass="my-custom-class"
        showBackdrop
        isOpen={showLoader}
        message={"Please wait..."}
      />

      <IonAlert
        isOpen={showError}
        onDidDismiss={() => setShowError(false)}
        header={"Error"}
        subHeader={"Something went wrong."}
        message={errorMessage}
        buttons={["OK"]}
      />
      <IonHeader>
        <IonToolbar color="primary">
          <IonTitle>One-Time Pin Validation</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
          <IonCard sx={styles.card}>
              <IonCardHeader>
                  <IonCardSubtitle>Please Enter the One-Time Pin sent to your phone:</IonCardSubtitle>
              </IonCardHeader>
              <IonCardContent>
                  <IonItem sx={styles.input}>
                  <IonLabel position='floating'>One-Time Pin</IonLabel>
                  <IonInput type='text' onIonChange={(e) => updateOtp(e)}/>
                  </IonItem>
                  <IonButton type='submit' shape='round' expand='block' onClick={buttonHandler}>Verify</IonButton>
              </IonCardContent>
          </IonCard>
      </IonContent>
    </IonPage>
  );
};

export default Otp;

const styles = {
    input:{
        mb: '4',
    },

    card:{
        mt: '28vh'
    },
};
