/** @jsxImportSource theme-ui */
import {
  IonButtons,
  IonContent,
  IonGrid,
  IonHeader,
  IonIcon,
  IonMenuButton,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import { information, informationCircle } from "ionicons/icons";
import React, { useState } from "react";
import ClientCard from "../../components/Card/ClientCard";

const Clients: React.FC = () => {
  const [clients, setClients] = useState([
    {
      clientId: "bhd",
      complexName: "Park Square",
      name: "Justin",
      surname: "Gabriel",
      cellNum: "",
      email: "",
      password: "",
      deviceId: "",
      otp: "",
    },
  ]);
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>Client Management</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonGrid>
          {clients[0].clientId !== "" && (
            <IonRow sx={styles.row}>
              <h2 sx={styles.heading.info}>
                <IonIcon icon={informationCircle} sx={styles.icon} />
                Click on a card if you would like to remove a client
              </h2>
            </IonRow>
          )}
          {clients[0].clientId !== "" &&
            clients.map((client) => (
              <ClientCard
                key={client.clientId}
                id={client.clientId}
                name={client.name}
                surname={client.surname}
                complexName={client.complexName}
              />
            ))}
          {clients[0].clientId === "" && (
            <h2 sx={styles.heading}>No Clients</h2>
          )}
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Clients;

const styles = {
  row: {
      m: '3',
      mt: '0',
      mb: '0',
      p:'2',
      pt:'0',
      border: '3px solid grey',
      borderRadius: '6px',
      opacity: '0.5'
  },
  heading: {
    display: "flex",
    justifyContent: "center",
    color: "grey",
    mt: "40vh",
    opacity: "0.5",

    info: {
      display: "flex",
      justifyContent: "center",
      color: "grey",
      fontSize: "15px",
    },
  },
  icon: {
    pr: "1vh",
    pt: "2px",
    size: "30px",
  },
};
