                </IonCardHeader>
              </IonCard>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonRow>
                    <IonCol size="8">
                      <IonCardTitle color="medium">
                        Package Popularity
                      </IonCardTitle>
                    </IonCol>
                  </IonRow>
                </IonCardHeader>
                <IonCardContent>
                  {packPopularityNames.length !== 0 &&
                    packPopularityValues.length !== 0 && <Pie data={state2} />}
                </IonCardContent>
              </IonCard>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonRow>
                    <IonCol size="8">
                      <IonCardTitle color="medium">
                        Client Population
                      </IonCardTitle>
                    </IonCol>
                  </IonRow>
                </IonCardHeader>
                <IonCardContent>
                  {packPopularityNames.length !== 0 &&
                    packPopularityValues.length !== 0 && (
                      <Doughnut data={state3} />
                    )}
                </IonCardContent>
              </IonCard>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonCardTitle color="medium">
                    Type of Bookings Today
                  </IonCardTitle>
                </IonCardHeader>
                <IonCardContent>
                  {packPopularityNames.length !== 0 &&
                    packPopularityValues.length !== 0 && (
                      <Doughnut data={state4} />
                    )}
                </IonCardContent>
              </IonCard>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonCardTitle color="medium">
                    Total Earnings For Today
                  </IonCardTitle>
                </IonCardHeader>
                <IonCardContent>
                  {packPopularityNames.length !== 0 &&
                    packPopularityValues.length !== 0 && (
                      <Doughnut data={state4} />
