/** @jsxImportSource theme-ui */
import {
  IonBackButton,
  IonButton,
  IonButtons,
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardSubtitle,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonInput,
  IonItem,
  IonLabel,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import React, { useState } from "react";
import { validOtp } from "../../components/Regex/Regex";

const RecoverOtp: React.FC = () => {
  const [otp, setOtp] = useState("");
  const [message, setMessage] = useState("");
  const [showError, setShowError] = useState(false);
  const [showLoader, setShowLoader] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  const updateOtp = (otp: any) => setOtp(otp);

  const resendOtp = () => {
      console.log("Yeah");
  };

  const buttonHandler = () => {
      if(!validOtp.test(otp)){
          setShowLoader(false);
          setMessage("Invalid Otp");
          setShowError(true);
      }else{
          console.log("true");
      }
  };
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons>
            <IonBackButton />
          </IonButtons>
          <IonTitle>Verify Otp</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonGrid>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonCardSubtitle style={{ textAlign: "center" }}>
                    Enter the otp that was sent to the respective choice of
                    medium
                  </IonCardSubtitle>
                </IonCardHeader>
                <IonCardContent>
                  <IonRow>
                    <IonCol>
                      <IonItem>
                        <IonLabel position="floating">One-Time Pin</IonLabel>
                        <IonInput
                          type="text"
                          value={otp}
                          onIonChange={(e) => updateOtp(e.detail.value)}
                        />
                      </IonItem>
                    </IonCol>
                  </IonRow>
                  <IonRow>
                      <IonCol>
                          <IonButton type="submit" expand="block" shape="round" sx={styles.button}>Verify</IonButton>
                      </IonCol>
                  </IonRow>
                  <IonRow>
                      <IonCol>
                          <p>Didn't recieve it ? <a onClick={()=>resendOtp()}>Resend One-Time Pin</a></p>
                      </IonCol>
                  </IonRow>
                </IonCardContent>
              </IonCard>
            </IonCol>
          </IonRow>
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default RecoverOtp;

const styles = {
    button: {
        pb: "4"
    },
};
