/** @jsxImportSource theme-ui */
import { jsx } from "theme-ui";
import {
  IonAlert,
  IonBackButton,
  IonButton,
  IonButtons,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonInput,
  IonItem,
  IonLabel,
  IonLoading,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";
import { validCellNum } from "../../components/Regex/Regex";
import Connection from "../../mixins/Connection";

const ForgotPassword: React.FC = () => {
  const path = useHistory();

  const [cellNum, setCellNum] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [showError, setShowError] = useState(false);
  const [showLoader, setShowLoader] = useState(false);

  const updateCellNum = (cellNum: any) => setCellNum(cellNum.target.value);

  const buttonHandler = () => {
    if (cellNum !== "") {
      if (validCellNum.test(cellNum)) {
        let url = "forgot/".concat(cellNum);
        Connection.processGetRequest({}, url, (response: any) =>
          mapResponse(response)
        );
      } else {
        setShowLoader(false);
        setErrorMessage("Invalid Cell Number.");
        setShowError(true);
      }
    } else {
      setShowLoader(false);
      setErrorMessage("You need to provide a Cell Number.");
      setShowError(true);
    }
  };

  const mapResponse = (response: any) => {
    if (response.type === "error") {
      setShowLoader(false);
      setErrorMessage(response.data.data);
      setShowError(true);
    } else {
      path.push("/otp");
    }
  };

  return (
    <IonPage>
      <IonLoading
        cssClass="my-custom-class"
        showBackdrop
        isOpen={showLoader}
        message={"Please wait..."}
      />

      <IonAlert
        isOpen={showError}
        onDidDismiss={() => setShowError(false)}
        header={"Error"}
        subHeader={"Something went wrong."}
        message={errorMessage}
        buttons={["OK"]}
      />
      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonBackButton defaultHref="/signIn" />
          </IonButtons>
          <IonTitle>Forgot Password</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonGrid sx={styles.grid}>
          <IonRow>
            <IonCol>
              <IonToolbar>
                <IonTitle size="small">
                  Please enter the cell number associated with your account
                </IonTitle>
              </IonToolbar>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonItem sx={styles.input}>
                <IonLabel position="floating">Cell Number</IonLabel>
                <IonInput type="text" onIonChange={(e) => updateCellNum(e)} />
              </IonItem>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonButton shape='round'type="submit" expand="block" onClick={buttonHandler}>
                Submit
              </IonButton>
            </IonCol>
          </IonRow>
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default ForgotPassword;

const styles = {
  input: {
    mb: 2,
  },
  grid: {
    mt: '60px',
    transform: "translate(0,50%)",
    padding: "10px",
  },
};
