import {
  IonAlert,
  IonBackButton,
  IonButton,
  IonButtons,
  IonCol,
  IonContent,
  IonFooter,
  IonGrid,
  IonHeader,
  IonImg,
  IonInput,
  IonItem,
  IonLabel,
  IonPage,
  IonRow,
  IonTitle,
  IonToast,
  IonToolbar,
} from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";
import TGWLogo from "../../components/TGWLogo.png";
import Connection from "../../mixins/Connection";


var message = '';


const mapResponse = (response: any) => {

};


const SignIn: React.FC = () => {
  const path = useHistory();

  const [showToast, setShowToast] = useState(false);
  const [showLoader, setShowLoader] = useState(false);
  const [showError, setShowError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [isFormValid, setIsFormValid] = useState(false);

  const [cellNum, setCellNum] = useState('');
  const [password, setPassword] = useState('');

  const updateCellNum = (cellNum: any) => {setCellNum(cellNum)};
  const updatePassword = (password: any) => {setPassword(password)};

  const buttonHandler = () => {
    validateForm();
    if(isFormValid){
    let url = "login".concat("/", cellNum, "/", password);
    Connection.processGetRequest({}, url, (response: any) => {
      mapResponse(response);
    })
  }
  };

  const validateForm = () => {
    if (cellNum === '' && password === ''){
      setIsFormValid(false);
      setErrorMessage("Fields cannot be empty!");
      setShowError(true);
  }
}

  const mapResponse = (response: any) => {
    if (response.type === "error") {
      setShowLoader(false);
      setErrorMessage(response.data);
      setShowError(true);
      setCellNum('');
      setPassword('');
    } else {
      setShowLoader(false);
      setShowToast(true);
      path.push("/dashboard");
    }
  };

  return (
    <IonPage>
      <IonContent>
          <IonImg src={TGWLogo} alt={"Logo.png"}/>
        <IonToolbar>
        <IonAlert
          isOpen={showError}
          onDidDismiss={() => setShowError(false)}
          header={'Error'}
          message={errorMessage}
          buttons={['OK']}
        />
          <IonGrid>
            <IonRow>
              <IonCol>
                <IonToolbar>
                  <IonTitle>Sign In</IonTitle>
                </IonToolbar>
              </IonCol>
            </IonRow>
            <IonRow>
              <IonCol>
                <IonItem>
                  <IonLabel position="floating">Cell Number</IonLabel>
                  <IonInput value={cellNum} onIonChange={e => updateCellNum(e.detail.value)}></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>
            <IonRow>
              <IonCol>
                <IonItem>
                  <IonLabel position="floating">Password</IonLabel>
                  <IonInput type="password" value={password} onIonChange={e => updatePassword(e.detail.value)}></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>
            <IonRow>
              <IonCol>
                <IonButton type="submit" expand="block" onClick={buttonHandler}>
                  SignIn
                </IonButton>
              </IonCol>
            </IonRow>
            <IonRow>
              <IonCol>
                <p>
                  Don't have an account ? <a href="/signUp">Sign Up.</a>
                </p>
              </IonCol>
            </IonRow>
          </IonGrid>
        </IonToolbar>
      </IonContent>
      <IonFooter>
       <IonToast
       isOpen={showToast}
       onDidDismiss={() => setShowToast(false)}
       message={message}
       duration={200}
     />
      </IonFooter>
    </IonPage>
  );
};

export default SignIn;
