import {
  IonButton,
  IonChip,
  IonCol,
  IonDatetime,
  IonGrid,
  IonIcon,
  IonInput,
  IonItem,
  IonItemDivider,
  IonLabel,
  IonRow,
} from "@ionic/react";
import { trash } from "ionicons/icons";
import React, { useState } from "react";
import Connection from "../../mixins/Connection";

const ComplexForm: React.FC<{
  name: string;
  isDisabled: boolean;
  content: any;
}> = (props) => {
  const {
    name,
    stree1,
    street2,
    city,
    province,
    postalCode,
    telephoneNum,
    startTime,
    endTime,
    cellNum,
    agents,
    units,
  } = props.content;

  var num = 0;

  const [unitList, setUnitList] = useState<string[]>(units);
  const [counter, setCounter] = useState<number>();

  const addUnit = (e: any) => {
    if (e.key === "Enter" && e.target.value !== '') {
      unitList.push(e.target.value);
      num = num + 1;
      setUnitList(unitList);
      console.log(unitList);
      console.log(unitList.indexOf(e.target.value))
      e.target.value = '';
    }
  };

  const removeUnit = (unit: any) => {
    let temp:any = [];
      for(let i = 0; i < unitList.length; i++){
          if(unitList[i] !== unit){
              temp.push(unitList[i]);
          }
      }
      setUnitList(temp);
      console.log(temp);
  };

  const submitHandler = () => {};

  let unitChips = unitList.map((unit) => (
    <IonChip key={unitList.indexOf(unit)} >
      <IonLabel key={unitList.indexOf(unit)}>{unit}</IonLabel>
      <IonIcon icon={trash} onClick={ e => removeUnit(unit)}/>
    </IonChip>
  ))

  return (
    <IonGrid>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Complex Name</IonLabel>
            <IonInput value={name}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Street 1</IonLabel>
            <IonInput value={street2}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Street 2</IonLabel>
            <IonInput value={street2}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">City</IonLabel>
            <IonInput value={city}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Province</IonLabel>
            <IonInput value={province}></IonInput>
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Postal code</IonLabel>
            <IonInput value={postalCode}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Telephone Number</IonLabel>
            <IonInput value={telephoneNum}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Operational Start Time</IonLabel>
            <IonDatetime displayFormat="+HH:mm" value={startTime.toString()}/>
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel>Operational End Time</IonLabel>
            <IonDatetime displayFormat="+HH:mm" value={endTime.toString()}/>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Cell Number</IonLabel>
            <IonInput value={cellNum}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItemDivider>
            <IonLabel>Units</IonLabel>
          </IonItemDivider>
          {unitList.map((unit, index) => (
            <IonChip key = {index}>
              <IonLabel>{unit}</IonLabel>
              <IonIcon icon={trash} onClick={ e => removeUnit(unit)}/>
            </IonChip>
          ))}
          <IonItem>
            <IonInput onKeyUp={(e) => addUnit(e)} />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton type="submit" expand="block" onClick={submitHandler}>
            {props.name}
          </IonButton>
        </IonCol>
      </IonRow>
    </IonGrid>
  );
};

export default ComplexForm;
