import {
  IonBackButton,
  IonButtons,
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  useIonViewDidEnter,
  useIonViewWillEnter,
} from "@ionic/react";
import React, { useState } from "react";
import { useParams } from "react-router";
import MultiForm from "../../components/MultiForm/MultiForm";
import InputControls from "../../components/InputControls/InputControls";
import Connection from "../../mixins/Connection";

var result;

const CardDetails: React.FC = () => {

  useIonViewWillEnter(()=>{
    if(type==="agent"){
      getAgent();
    }else if(type==="complex"){
      getComplex();
    }else if(type==="package"){
      getPackage();
    }else{
      getPromotion();
    }
  })
  const [selectedSegment, setSelectedSegment] = useState<"Update" | "Delete">("Update");
  const [isDisabled, setIsDisabled] = useState<true | false>(false);
  const [showLoader, setShowLoader] = useState(false);
  const [showError, setShowError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const { name, id ,type, back } = useParams<{
    name: string;
    id: string;
    type: "agent" | "complex" | "package" | "promotion";
    back: string;
  }>();

  const selectedSegmentHandler = (selectedValue: "Update" | "Delete") => {
    setSelectedSegment(selectedValue);
    if (selectedValue == "Delete") {
      setIsDisabled(true);
    } else {
      setIsDisabled(false);
    }
  };

  let agent =
    {
      agentId: "",
      complexName: "",
      contractId: "",
      name: "",
      surname: "",
      id: "",
      cellNum: "",
      street1: "",
      street2: "",
      city: "",
      province: "",
      postalCode: "",
      password: "",
    }

  let complex =
    {
      complexId: "",
      complexName: "",
      street1: "",
      street2: "",
      city: "",
      province: "",
      postalCode: "",
      telephoneNum: "",
      startTime: new Date,
      endTime: new Date,
      cellNum: "",
      units: [],
      agents: [],
    }
  let pack =
    {
      packageId: "",
      promotionId: "",
      packageName: "",
      minutes: 0,
      standardPrice: "",
      suvPrice: "",
      description: "",
      onPromotion: true,
    }
  let promotion =
    {
      promotionId: "",
      promotionName: "",
      packageName: "",
      standardPrice: "",
      suvPrice: "",
      isEnabled: false,
    }

    let emptyPack = {
    packageId: "",
    promotionId: "",
    packageName: "",
    minutes: 0,
    standardPrice: "",
    suvPrice: "",
    description: "",
    onPromotion: false,
  }

  const getAgent = () => {
    var url = "agent/get/".concat(id);
    Connection.processGetRequest({}, url, (response: any) => {
      mapAgent(response);
    });
  };
  const getComplex = () => {
    var url = "complex/get/".concat(id);
    Connection.processGetRequest({}, url, (response: any) => {
      mapComplex(response);
    });
  };
  const getPackage = () => {
    var url = "package/get/".concat(id);

    Connection.processGetRequest({}, url, (response: any) => {
      mapPackage(response);
    });
  };
  const getPromotion = () => {
    var url = "promotion/get/".concat(id);
    Connection.processGetRequest({}, url, (response: any) => {
      mapPromotion(response);
    });
  };

  const mapPackage = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      pack = response.data.data;
    }
  };
  const mapAgent = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      agent = response.data.data
    }
  };

  const mapComplex = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      complex = response.data.data;
    }
  };
  const mapPromotion = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      promotion = response.data.data;
    }
  };



  const url = "/page/";
  /*Assign Content from API*/
  if (type === "agent") {
    result = agent;

  } else if (type === "complex") {
    result = complex;

  } else if (type === "package") {
    result = pack;  
  } else {
    result = promotion;
  }

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton
              defaultHref={url.concat(back).concat("/").concat(type)}
            />
          </IonButtons>
          <IonTitle>{name}</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <InputControls
          selectedValue={selectedSegment}
          onSelectedValue={selectedSegmentHandler}
        />
        <>
        {result !== emptyPack && <MultiForm
          buttonName={selectedSegment}
          type={type}
          isDisabled={isDisabled}
          content={result}
        />}
        </>
      </IonContent>
    </IonPage>
  );
};

export default CardDetails;
