import {
  IonButton,
  IonChip,
  IonCol,
  IonDatetime,
  IonGrid,
  IonIcon,
  IonInput,
  IonItem,
  IonItemDivider,
  IonLabel,
  IonRow,
} from "@ionic/react";
import { trash } from "ionicons/icons";
import React, { useState } from "react";
import Connection from "../../mixins/Connection";

interface Complex {
  complexId: string;
  complexName: string;
  street1: string;
  street2: string;
  city: string;
  province: string;
  postalCode: string;
  telephoneNum: string;
  startTime: Date;
  endTime: Date;
  cellNum: string;
  units: string[];
  agents: string[];
}

const ComplexForm: React.FC<{
  name: string;
  isDisabled: boolean;
  content: Complex;
}> = (props) => {



  var {
    complexId,
    complexName,
    street1,
    street2,
    city,
    province,
    postalCode,
    telephoneNum,
    startTime,
    endTime,
    cellNum,
    agents,
    units,
  } = props.content;

  const [enteredComplexName, setEnteredComplexName] = useState(complexName);
  const [enteredStreet1, setEnteredStreet1] = useState(street1);
  const [enteredStreet2, setEnteredStreet2] = useState(street2);
  const [enteredCity, setEnteredCity] = useState(city);
  const [enteredProvince, setEnteredProvince] = useState(province);
  const [enteredPostalCode, setEnteredPostalCode] = useState(postalCode);
  const [enteredTelNum, setEnteredTelNum] = useState(telephoneNum);
  const [enteredStartTime, setEnteredStartTime] = useState(startTime.toString());
  const [enteredEndTime, setEnteredEndTime] = useState(endTime.toString());
  const [enteredCellNum, setEnteredCellNum] = useState(cellNum);
  const [enteredUnits, setEnteredUnits] = useState(units);

  var num = 0;

  const [unitList, setUnitList] = useState<string[]>(enteredUnits);

  const addUnit = (e: any) => {
    if (e.key === "Enter" && e.target.value !== '') {
      unitList.push(e.target.value);
      num = num + 1;
      setUnitList(unitList);
      console.log(unitList);
      console.log(unitList.indexOf(e.target.value))
      e.target.value = '';
    }
  };

  const removeUnit = (unit: any) => {
    let temp:any = [];
      for(let i = 0; i < unitList.length; i++){
          if(unitList[i] !== unit){
              temp.push(unitList[i]);
          }
      }
      setUnitList(temp);
      console.log(temp);
  };

  const submitHandler = () => {
    if(props.name === "Update"){

    }
    else if(props.name === "Delete"){

    }else if(props.name === "Add"){
       let url = "complex/add"
       const payload = {
         complexId: complexId,
         complexName: enteredComplexName,
         street1: enteredStreet1,
         street2: enteredStreet2,
         city: enteredCity,
         province: enteredProvince,
         postalCode: enteredPostalCode,
         telephoneNum: enteredTelNum,
         startTime: enteredStartTime,
         endTime: enteredEndTime,
         agents: agents,
         units: enteredUnits
       }
       Connection.processPostRequest(payload, url, (response: any) =>{})
    }
  };

  let unitChips = unitList.map((unit) => (
    <IonChip key={unitList.indexOf(unit)} >
      <IonLabel>{unit}</IonLabel>
      <IonIcon icon={trash} onClick={ e => removeUnit(unit)}/>
    </IonChip>
  ))

  return (
    <IonGrid>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Complex Name</IonLabel>
            <IonInput value={enteredComplexName} disabled={props.isDisabled}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Street 1</IonLabel>
            <IonInput value={enteredStreet1} disabled={props.isDisabled}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Street 2</IonLabel>
            <IonInput value={enteredStreet2} disabled={props.isDisabled}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">City</IonLabel>
            <IonInput value={enteredCity} disabled={props.isDisabled}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Province</IonLabel>
            <IonInput value={enteredProvince} disabled={props.isDisabled}></IonInput>
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Postal code</IonLabel>
            <IonInput value={enteredPostalCode} disabled={props.isDisabled}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Telephone Number</IonLabel>
            <IonInput value={enteredTelNum} disabled={props.isDisabled}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Operational Start Time</IonLabel>
            <IonDatetime displayFormat="+HH:mm" value={enteredStartTime} disabled={props.isDisabled}/>
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel>Operational End Time</IonLabel>
            <IonDatetime displayFormat="+HH:mm" value={enteredEndTime} disabled={props.isDisabled}/>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Cell Number</IonLabel>
            <IonInput value={enteredCellNum} disabled={props.isDisabled}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItemDivider>
            <IonLabel>Units</IonLabel>
          </IonItemDivider>
          {unitList.map((unit, index) => (
            <IonChip key = {index}>
              <IonLabel>{unit}</IonLabel>
              <IonIcon icon={trash} onClick={ e => removeUnit(unit)}/>
            </IonChip>
          ))}
          <IonItem>
            <IonInput onKeyUp={(e) => addUnit(e)} disabled={props.isDisabled}/>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton type="submit" expand="block" onClick={submitHandler}>
            {props.name}
          </IonButton>
        </IonCol>
      </IonRow>
    </IonGrid>
  );
};

export default ComplexForm;
