import { IonButtons, IonContent, IonFab, IonFabButton, IonHeader, IonIcon, IonMenuButton, IonPage, IonTitle, IonToolbar } from '@ionic/react';
import { add, card } from 'ionicons/icons';
import { useState } from 'react';
import { useParams } from 'react-router';
import Card from '../../components/Card/Card';
import Connection from '../../mixins/Connection';
import './Page.css';

const Management: React.FC = () => {

  const [ showLoader, setShowLoader ] = useState(false);
  const [ showError, setShowError ] = useState(false);
  const [ errorMessage, setErrorMessage ] = useState('');

  const { name, title} = useParams<{ name: string, title: "agent" | "complex" | "package" | "promotion"}>();
  const [ packages, setPackages ] = useState([{
      packageId: "",
      promotionId: "",
      packageName: "",
      minutes: 0,
      standardPrice: "",
      suvPrice: "",
      description: "",
      onPromotion: false,
  }]);

  const getPackages = () =>{
    var url = 'package/getAll';

    Connection.processGetRequest({}, url, (response: any) => {
      mapResponse(response);
    })
  }

  const mapResponse = (response: any) => {
    if(response.type === 'error'){
      setErrorMessage(response.data)
      setShowError(true);
    }
    else{
      setPackages(response.data.data);
    }
  }

  const addUrl = "/add/"

  interface Result {
    id: string;
    title: string;
    subtitle: string;
  }

  interface Agent {
    agentId: string;
    complexName: string;
    contractId: string;
    name: string;
    surname: string;
    id: string;
    cellNum: string;
    street1: string;
    street2: string;
    city: string;
    province: string;
    postalCode: string;
    password: string;
  }

  interface Complex {
    complexId: string;
    name: string;
    street1: string;
    street2: string;
    city: string;
    province: string;
    postalCode: string;
    telephoneNum: string;
    startTime: string;
    endTime: string;
    cellNum: string;
    units: [];
    agents: [];
  }

  interface Package {
    packageId: string;
    promotionId: string;
    packageName: string;
    minutes: number;
    standardPrice: string;
    suvPrice: string;
    description: string;
    onPromotion: boolean;
  }

  interface Promotion {
    promotionId: string;
    promotionName: string;
    packageName: string;
    standardPrice: string;
    suvPrice: string;
    isEnabled: boolean;
  }

  var result;

  if (title === "agent") {
    const agent: Agent = {
      agentId: "",
      complexName: "",
      contractId: "",
      name: "",
      surname: "",
      id: "",
      cellNum: "",
      street1: "",
      street2: "",
      city: "",
      province: "",
      postalCode: "",
      password: "",
    };

  } else if (title === "complex") {
    const complex: Complex = {
      complexId: "",
      name: "",
      street1: "",
      street2: "",
      city: "",
      province: "",
      postalCode: "",
      telephoneNum: "",
      startTime: "",
      endTime: "",
      cellNum: "",
      units: [],
      agents: [],
    };

  } else if (title === "package") {
    getPackages()

    result = packages;
  } else {
    const promotion: Promotion = {
      promotionId: "",
      promotionName: "",
      packageName: "",
      standardPrice: "",
      suvPrice: "",
      isEnabled: false,
    };

  }
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>{name.concat(" Management")}</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen>
        {packages.map(pack =>
        <Card type={title} id={pack.packageId} title={pack.packageName} subtitle={pack.standardPrice} reference={name}/>
        )
        }
        <IonFab vertical="bottom" horizontal="end" slot="fixed">
          <IonFabButton href={addUrl.concat(name,"/",title)}>
            <IonIcon icon={add} />
          </IonFabButton>
        </IonFab>
      </IonContent>
    </IonPage>
  );
};

export default Management;
