/** @jsxImportSource theme-ui */
import {
  IonButtons,
  IonContent,
  IonGrid,
  IonHeader,
  IonMenuButton,
  IonPage,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import React, { useState } from "react";
import ClientCard from "../../components/Card/ClientCard";

const Clients: React.FC = () => {
  const [clients, setClients] = useState([
    {
      clientId: "",
      complexName: "Park Square",
      name: "Justin",
      surname: "Gabriel",
      cellNum: "",
      email: "",
      password: "",
      deviceId: "",
      otp: "",
    },
  ]);
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>Client Management</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonGrid>
          {clients[0].clientId === "" && (
            <h2 sx={styles.heading.info}>
              Click on a card if you would like to remove a client
            </h2>
          )}
          {clients[0].clientId !== "" &&
            clients.map((client) => (
              <ClientCard
                key={client.clientId}
                id={client.clientId}
                name={client.name}
                surname={client.surname}
                complexName={client.complexName}
              />
            ))}
          {clients[0].clientId === "" && (
            <h2 sx={styles.heading}>No Clients</h2>
          )}
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Clients;

const styles = {
  heading: {
    display: "flex",
    justifyContent: "center",
    color: "grey",
    mt: "40vh",
    opacity: "0.5",

    info: {
      display: "flex",
      justifyContent: "center",
      color: "grey",
    },
  },
};
