/** @jsxImportSource theme-ui */
import { Storage } from "@capacitor/storage";
import {
  IonAlert,
  IonBackButton,
  IonButton,
  IonButtons,
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardSubtitle,
  IonCardTitle,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonInput,
  IonItem,
  IonLabel,
  IonLoading,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import { verify } from "crypto";
import React, { useState } from "react";
import { useHistory } from "react-router";
import { validOtp } from "../../components/Regex/Regex";
import Connection from "../../mixins/Connection";

const Otp: React.FC = () => {
  const path = useHistory();
  const [otp, setOtp] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [showError, setShowError] = useState(false);
  const [showLoader, setShowLoader] = useState(false);

  const updateOtp = (otp: any) => setOtp(otp.target.value);

  const buttonHandler = () => {
    if (otp === "") {
      setShowLoader(false);
      setErrorMessage("Please enter One-Time Pin.");
      setShowError(true);
    } else if (!validOtp.test(otp)) {
      setShowLoader(false);
      setErrorMessage("Invalid One-Time Pin.");
      setShowError(true);
    } else {
      getAdminId();
    }
  };

  const getAdminId = async () => {
    const response: any = await Storage.get({
      key: "adminId",
    });
    const id = response.value;

    let date = new Date();
    let time = date.toISOString();

    verifyOtp(time, id);
  };

  const verifyOtp = (time: any, id: any) => {
    let url = "forgot/verifyOtp/".concat(otp, "/", time, "/", 'sechele.rk@gmail.com');
    Connection.processPostRequest({}, url, (response: any) =>
      mapResponse(response)
    );
  };

  const mapResponse = (response: any) => {
    if (response.type === "error") {
      setShowLoader(false);
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      setShowLoader(false);
      console.log(response.data.data)
      path.push("/newPassword");
    }
  };

  return (
    <IonPage>
      <IonLoading
        cssClass="my-custom-class"
        showBackdrop
        isOpen={showLoader}
        message={"Please wait..."}
      />

      <IonAlert
        isOpen={showError}
        onDidDismiss={() => setShowError(false)}
        header={"Error"}
        subHeader={"Something went wrong."}
        message={errorMessage}
        buttons={["OK"]}
      />
      <IonHeader>
        <IonToolbar color="primary">
          <IonTitle>One-Time Pin Validation</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonCard sx={styles.card}>
          <IonCardHeader>
            <IonCardSubtitle>
              Please Enter the One-Time Pin sent to your phone:
            </IonCardSubtitle>
          </IonCardHeader>
          <IonCardContent>
            <IonItem sx={styles.input}>
              <IonLabel position="floating">One-Time Pin</IonLabel>
              <IonInput type="text" onIonChange={(e) => updateOtp(e)} />
            </IonItem>
            <IonButton
              type="submit"
              shape="round"
              expand="block"
              sx={styles.button}
              onClick={() => buttonHandler()}
            >
              Verify
            </IonButton>
          </IonCardContent>
        </IonCard>
      </IonContent>
    </IonPage>
  );
};

export default Otp;

const styles = {
  button:{
    padding: '3'
  },
  input: {
    mb: "4",
  },

  card: {
    mt: "28vh",
  },
};
