import {
  IonButton,
  IonCol,
  IonDatetime,
  IonGrid,
  IonIcon,
  IonInput,
  IonItem,
  IonItemDivider,
  IonItemGroup,
  IonLabel,
  IonRow,
} from "@ionic/react";
import { add, remove } from "ionicons/icons";
import React, { ReactComponentElement, useState } from "react";
import Connection from "../../mixins/Connection";

const ComplexForm: React.FC<{
  name: string;
  isDisabled: boolean;
  content: any;
}> = (props) => {
  const {
    complexName,
    stree1,
    street2,
    city,
    province,
    postalCode,
    telephoneNum,
    startTime,
    endTime,
    cellNum,
    agents,
    units,
  } = props.content;

  const [unitInputList, setUnitInputList] = useState<any>([]);

  let temp: string[];

  const onUnitAddClick = () => {
    setUnitInputList(
      unitInputList.concat(
        <IonItem>
          <IonInput id={unitInputList.length} key={unitInputList.length} />
        </IonItem>
      )
    );
  };

  const submitHandler = () => {
      if(props.name === "Add"){
        let url = "complex/add";

        // var payload = {
        //     complexName: comName,
        //     stree1: str1,
        //     street2: str2,
        //     city: cty,
        //     province: prov,
        //     postalCode: pos,
        //     telephoneNum: telNum,
        //     startTime: sTime,
        //     endTime: eTime,
        //     cellNum: cNum,
        //     agents: agents,
        //     units: un
        //   }

      }else if(props.name === "Update"){
        let url = "complex/update";

      }else{
        let url = "complex/remove/";

      }
  };

  if (units !== undefined) {
    setUnitInputList(
      units.map((agent: string) => (
        <IonItem>
          <IonInput value={agent} />
        </IonItem>
      ))
    );
  }
  return (
    <IonGrid>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Complex Name</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Street 1</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Street 2</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>City</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Province</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel>Postal code</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Telephone Number</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Operational Start Time</IonLabel>
            <IonDatetime displayFormat="HH:mm" />
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel>Operational End Time</IonLabel>
            <IonDatetime displayFormat="HH:mm" />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Cell Number</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItemGroup>
            <IonItemDivider>
              <IonLabel>Units</IonLabel>
              <IonButton slot="end" type="button" disabled={props.isDisabled} onClick={onUnitAddClick}>
              <IonIcon icon={add} />
            </IonButton>
            <IonButton slot="end" type="button" disabled={props.isDisabled} onClick={onUnitAddClick}>
              <IonIcon icon={remove} />
            </IonButton>
            </IonItemDivider> 
            {unitInputList}
          </IonItemGroup>
        </IonCol>
      </IonRow>
      <IonRow>
          <IonCol>
              <IonButton type="submit" onClick={submitHandler}>
                  {props.name}
              </IonButton>
          </IonCol>
      </IonRow>
    </IonGrid>
  );
};

export default ComplexForm;
