import {
  IonButton,
  IonCol,
  IonGrid,
  IonInput,
  IonItem,
  IonLabel,
  IonRow,
  IonToggle,
} from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";
import Connection from "../../mixins/Connection";

const PromotionForm: React.FC<{ name: string; isDisabled: boolean; content: any}> = (
  props
) => {

  var {
    promotionId,
    promotionName,
    packageName,
    standardPrice,
    suvPrice,
    isEnabled
  } = props.content;

  const [enteredPromotionName, setPromotionName] = useState(promotionName);
  const [enteredPackageName, setPackageName] = useState(packageName);
  const [enteredStandardPrice, setStandardPriceName] = useState(standardPrice);
  const [enteredSuvPrice, setSuvPrice] = useState(suvPrice);
  const [enteredIsEnabled, setIsEnabled] = useState(isEnabled);

  const updatePromotionName = (promotionName: any) => setPromotionName(enteredPromotionName);
  const updatePackageName = (packageName: any) => setPackageName(enteredPackageName);
  const updateStandardPrice = (standardPrice: any) => setStandardPriceName(enteredStandardPrice);
  const updateSuvPrice = (suvPrice: any) => setSuvPrice(enteredSuvPrice);
  const updateIsEnabled = (isEnabled: any) => setIsEnabled(enteredIsEnabled);

  const path = useHistory();

  const buttonHandler = () => {
    if(props.name === "Update"){
      let url = "promotion/update"
      var payload = {
        promotionId: promotionId,
        packageName: enteredPackageName,
        standardPrice: enteredStandardPrice,
        suvPrice: enteredSuvPrice,
        isEnabled: enteredIsEnabled
      }

      Connection.processPostRequest(payload, url, (response: any) => {
        mapUpdateResponse(response);
      })

    }
    else if(props.name === "Delete"){
      let url = "promotion/remove/".concat(promotionId);

      Connection.processDeleteRequest({}, url, (response: any) => {
        mapDeleteResponse(response);
      })

    }
    else if(props.name === "Add"){
      let url = "promotion/add"
      var payload = {
        promotionId: promotionId,
        packageName: enteredPackageName,
        standardPrice: enteredStandardPrice,
        suvPrice: enteredSuvPrice,
        isEnabled: enteredIsEnabled
      }
      Connection.processPostRequest(payload, url, (response: any) => {
        mapAddResponse(response);
      })
    }
  };

  const mapUpdateResponse = (response: any) => {};
  const mapDeleteResponse = (response: any) => {};
  const mapAddResponse = (response: any) => {};
  return (
    <IonGrid>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Promotion Name</IonLabel>
            <IonInput disabled={props.isDisabled} value={promotionName} onIonChange={e => updatePromotionName(e.detail.value)}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Package Name</IonLabel>
            <IonInput disabled={props.isDisabled} value={packageName} onIonChange={e => updatePackageName(e.detail.value)}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Standard Price</IonLabel>
            <IonInput disabled={props.isDisabled} value={standardPrice} onIonChange={e => updateStandardPrice(e.detail.value)}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Suv Price</IonLabel>
            <IonInput disabled={props.isDisabled} value={suvPrice} onIonChange={e => updateSuvPrice(e.detail.value)}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Enabled</IonLabel>
            <IonToggle checked={isEnabled} disabled={props.isDisabled} onIonChange={e => updateIsEnabled(e.detail.value)}/>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton type="submit" expand="block">
            {props.name}
          </IonButton>
        </IonCol>
      </IonRow>
    </IonGrid>
  );
};

export default PromotionForm;
