import {
  IonButton,
  IonCol,
  IonGrid,
  IonInput,
  IonItem,
  IonLabel,
  IonRow,
  IonToggle,
} from "@ionic/react";
import React, { useState } from "react";
import Connection from "../../mixins/Connection";

const PackageForm: React.FC<{ name: string; isDisabled: boolean; content: any}> = (
  props
) => {
  const {
    packageId,
    packageName,
    minutes,
    standardPrice,
    suvPrice,
    description,
    onPromotion
  } = props.content;

  const [ showSuccess, setShowSuccess ] = useState(false);
  const [ showError, setShowError ] = useState(false);
  const [ errorMessage, setErrorMessage ] = useState('');

  const buttonHandler =()=>{
    if(props.name === "Update"){
      var url = "package/update";
      var pack = {
        packageId: packageId,
        packageName: packageName,
        standardPrice: standardPrice,
        suvPrice: suvPrice,
      }

      Connection.processPostRequest(pack, url, (response:any) => {
        mapResponse(response);
      })

    }else if(props.name === "Delete"){
      url = "package/delete"
    }else{
      url = "package/add"
      var pack = {
        packageId: packageId,
        packageName: packageName,
        standardPrice: standardPrice,
        suvPrice: suvPrice,
      }
      Connection.processPostRequest(pack, url, (response:any) => {
        mapResponse(response);
      })
    }
  }

  const mapResponse = (response: any) => {

  };
  return (
    <IonGrid>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Package Name</IonLabel>
            <IonInput disabled={props.isDisabled} value={packageName}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Minutes</IonLabel>
            <IonInput disabled={props.isDisabled} value={minutes}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Standard Price</IonLabel>
            <IonInput disabled={props.isDisabled} value={standardPrice}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">SUV Price</IonLabel>
            <IonInput disabled={props.isDisabled} value={suvPrice}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Description</IonLabel>
            <IonInput disabled={props.isDisabled} value={description}></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">On Promotion</IonLabel>
            <IonToggle disabled={true} value={onPromotion}></IonToggle>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton type="submit" expand="block">
            {props.name}
          </IonButton>
        </IonCol>
      </IonRow>
    </IonGrid>
  );
};

export default PackageForm;
