import {
    IonAlert,
  IonCard,
  IonCardHeader,
  IonCardSubtitle,
  IonCardTitle,
  IonCol,
  IonRow,
} from "@ionic/react";
import React, { useState } from "react";

const ClientCard: React.FC<{
  complexName: any;
  name: any;
  surname: any;
  id: any;
}> = (props) => {
    const [showOptions, setShowOptions] = useState(false);
    const [showConfirm, setShowConfirm] = useState(false);
    const handler = () => {
        setShowOptions(true);
    };
  return (
    <>
    <IonAlert
          isOpen={showOptions}
          onDidDismiss={() => setShowOptions(false)}
          header={'Warning'}
          message={'By removing this client, you are revoking their access to this application and permanently removing them from your system ?'}
          buttons={[
            {
              text: "Cancel",
              cssClass: "secondary",
            },
            {
              text: "Continue",
              handler: () => {
                setShowOptions(false);
                setShowConfirm(true);
              },
            },
          ]}
        />
        <IonAlert
          isOpen={showConfirm}
          onDidDismiss={() => setShowConfirm(false)}
          header={'Confirm'}
          message={'Are you sure ?'}
          buttons={[
            {
              text: "No",
              cssClass: "secondary",
            },
            {
              text: "Yes",
              handler: () => {
                setShowConfirm(false);
                //delete client
              },
            },
          ]}
        />
      <IonRow>
        <IonCol>
          <IonCard color="secondary" button={true} onClick={handler}>
            <IonCardHeader>
              <IonCardTitle>
                {props.name} {props.surname}
              </IonCardTitle>
              <IonCardSubtitle>{props.complexName}</IonCardSubtitle>
            </IonCardHeader>
          </IonCard>
        </IonCol>
      </IonRow>
    </>
  );
};

export default ClientCard;
