/** @jsxImportSource theme-ui */
import { Storage } from "@capacitor/storage";
import {
  IonAlert,
  IonButton,
  IonCol,
  IonGrid,
  IonInput,
  IonItem,
  IonLabel,
  IonLoading,
  IonRow,
  useIonViewWillEnter,
} from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";
import Connection from "../../mixins/Connection";

import { validCellNum, validPassword, validName } from "../Regex/Regex";

interface Admin {
  adminId: string;
  name: string;
  surname: string;
  cellNum: string;
  password: string;
}

const AdminForm: React.FC<{ buttonName: string; content: Admin; isDisabled: boolean}> = (props) => {
  const { adminId, name, surname, cellNum, password } = props.content;

  useIonViewWillEnter(()=>{
    if(props.buttonName === "Update"){
      setShowLoader(true);
      getAdminId();
    }
  })

  const [admin, setAdmin] = useState({
    adminId: "",
    name: "",
    surname: "",
    cellNum: "",
    email: "",
    password: ""
  }); 

  const getAdminId = async() => {
    const id: any = await Storage.get({key: 'adminId'});
    const adminId = id.value;

    getAdminDetails(adminId);
  };

  const getAdminDetails = (adminId: string) => {
    let url = 'get/'.concat(adminId);
    Connection.processGetRequest({}, url, (response: any)=>{
      mapResponse(response);
    })
  };
  const mapResponse = (response: any) => {
    if (response.type === "error") {
      setShowLoader(false);
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      setAdmin(response.data.data);
      setEnteredName(admin.name);
      setEnteredSurname(admin.surname);
      setEnteredCellNum(admin.cellNum);
      setEnteredEmail(admin.email);
      setShowLoader(false);
    }
  };

  const [showLoader, setShowLoader] = useState(false);
  const [showError, setShowError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [showSuccess, setShowSuccess] = useState(false);
  const [isFormValid, setIsFormValid] = useState(false);

  const [enteredName, setEnteredName] = useState(name);
  const [enteredSurname, setEnteredSurname] = useState(surname);
  const [enteredCellNum, setEnteredCellNum] = useState(cellNum);
  const [enteredEmail, setEnteredEmail] = useState(cellNum);
  const [enteredPassword, setEnteredPassword] = useState(password);
  const [confirmPassword, setConfirmPassword] = useState("");

  const updateName = (name: any) => {
    setEnteredName(name);
    console.log(display.name);
  };
  const updateSurname = (surname: any) => {
    setEnteredSurname(surname);
  };
  const updateCellNum = (cellNum: any) => {
    setEnteredCellNum(cellNum);
  };
  const updateEmail = (email: any) => {
    setEnteredEmail(email);
  };
  const updatePassword = (password: any) => {
    setEnteredPassword(password);
  };
  const updateConfirmPassword = (confirmPassword: any) => {
    setConfirmPassword(confirmPassword);
  };

  let path = useHistory();

  const buttonHandler = () => {
    validateForm();
    if (isFormValid === true) {
      if (props.buttonName === "Update") {
        let url = "update";

        let payload = {
          adminId: adminId,
          name: enteredName,
          surname: enteredSurname,
          cellNum: enteredCellNum,
          email: enteredEmail,
          password: enteredPassword,
        };
        Connection.processPostRequest(payload, url, (response: any) => {
          mapUpdateResponse(response);
        });

      } else if (props.buttonName === "Delete") {
        setShowLoader(false);
        let path = "remove/";
        console.log(path);
      } else {
        let url = "signUp";
        let payload = {
          adminId: adminId,
          name: enteredName,
          surname: enteredSurname,
          cellNum: enteredCellNum,
          email: enteredEmail,
          password: enteredPassword,
        };

        Connection.processPostRequest(payload, url, (response: any) => {
          mapPostResponse(response);
        });
      }
    }
  };

  const mapUpdateResponse = (response: any) => {
    setShowLoader(false);
    if (response.type === "error") {
      setShowLoader(false);
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      setShowLoader(false);
      setSuccessMessage("Profile Updated!");
      setShowSuccess(true);
    }
  };

  const mapPostResponse = (response: any) => {
    setShowLoader(false);
    if (response.type === "error") {
      setShowLoader(false);
      setErrorMessage(response.data);
      setShowError(true);
    } else {
      setShowLoader(false);
      setSuccessMessage("Details Successfully Captured!");
      setShowSuccess(true);
    }
  };

  const validateForm = () => {
    if (
      enteredName === "" ||
      enteredSurname === "" ||
      enteredCellNum === "" ||
      enteredPassword === ""
    ) {
      setShowLoader(false);
      setErrorMessage("Fields must not be left empty.");
      setShowError(true);
      setIsFormValid(false);
    } else if (enteredPassword !== confirmPassword) {
      setShowLoader(false);
      setErrorMessage(
        "Passwords do not match. Please ensure they are entered correctly!"
      );
      setShowError(true);
      setIsFormValid(false);
    } else {
      if (!validName.test(enteredName)) {
        setShowLoader(false);
        setErrorMessage("Invalid Name.");
        setShowError(true);
        setIsFormValid(false);
      } else if (!validName.test(enteredSurname)) {
        setShowLoader(false);
        setErrorMessage("Invalid Surname.");
        setShowError(true);
        setIsFormValid(false);
      } else if (!validCellNum.test(enteredCellNum)) {
        setShowLoader(false);
        setErrorMessage("Invalid Cell Number.");
        setShowError(true);
        setIsFormValid(false);
      } else if (!validPassword.test(enteredPassword)) {
        setShowLoader(false);
        setErrorMessage(
          "Password must be 8-16 characters long. Ensure it includes atleast 1 aphabet character and 1 digit."
        );
        setShowError(true);
        setIsFormValid(false);
      } else {
        setIsFormValid(true);
      }
    }
  };
  return (
    <IonGrid >
      <IonLoading
        cssClass="my-custom-class"
        showBackdrop
        isOpen={showLoader}
        message={"Please wait..."}
      />

      <IonAlert
        isOpen={showError}
        onDidDismiss={() => setShowError(false)}
        header={"Error"}
        subHeader={"Something went wrong."}
        message={errorMessage}
        buttons={["OK"]}
      />

      <IonAlert
        isOpen={
         showSuccess
        }
        onDidDismiss={
            () => path.push("/signIn")
        }
        header={"Success"}
        subHeader={successMessage}
        buttons={["OK"]}
      />
      <IonRow>
        <IonCol>
          <IonItem sx={styles.item}>
            <IonLabel position="floating">Name  <span sx={styles.arterisk.name}>*</span></IonLabel>
            <IonInput
              type="text"
              value={enteredName}
              disabled={props.isDisabled}
              onIonChange={(e) => updateName(e.detail.value)}
            />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem sx={styles.item}>
            <IonLabel position="floating">Surname  <span sx={styles.arterisk.surname}>*</span></IonLabel>
            <IonInput
              type="text"
              value={enteredSurname}
              disabled={props.isDisabled}
              onIonChange={(e) => updateSurname(e.detail.value)}
            />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem sx={styles.item}>
            <IonLabel position="floating">Cell Number  <span sx={styles.arterisk.cellNum}>*</span></IonLabel>
            <IonInput
              type="text"
              value={enteredCellNum}
              disabled={props.isDisabled}
              onIonChange={(e) => updateCellNum(e.detail.value)}
            />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem sx={styles.item}>
            <IonLabel position="floating">Email <span sx={styles.arterisk.email}>*</span></IonLabel>
            <IonInput
              type="text"
              value={enteredCellNum}
              disabled={props.isDisabled}
              onIonChange={(e) => updateEmail(e.detail.value)}
            />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem sx={styles.item}>
            <IonLabel position="floating">Password  <span sx={styles.arterisk.pass}>*</span></IonLabel>
            <IonInput
              type="password"
              value={enteredPassword}
              disabled={props.isDisabled}
              onIonChange={(e) => updatePassword(e.detail.value)}
            />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem sx={styles.item}>
            <IonLabel position="floating">Confrim Password  <span sx={styles.arterisk.confirm}>*</span></IonLabel>
            <IonInput
              type="password"
              value={confirmPassword}
              disabled={props.isDisabled}
              onIonChange={(e) => updateConfirmPassword(e.detail.value)}
            />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton
            shape="round"
            type="submit"
            expand="block"
            onClick={buttonHandler}
          >
            {props.buttonName}
          </IonButton>
        </IonCol>
      </IonRow>
    </IonGrid>
  );
};

