import {
  IonAlert,
  IonButton,
  IonButtons,
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardTitle,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonLoading,
  IonMenuButton,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
  useIonViewDidEnter,
  useIonViewDidLeave,
  useIonViewWillEnter,
  useIonViewWillLeave,
} from "@ionic/react";
import React, { useState } from "react";
import { Bar, Doughnut, Line, Pie } from "react-chartjs-2";
import Connection from "../../mixins/Connection";

interface ResponseObject{
  name: string,
  value: number
}
interface Admin {
  adminId: string;
  name: string;
  surname: string;
  cellNum: string;
  password: string;
}

const Dashboard: React.FC = () => {

useIonViewWillEnter(()=>{
  setShowLoader(true);
  getAdminDetails(adminId);
  getPast7Days();
  getPopularity();
  getPopulation();
  console.log(complexNames);
  console.log(packPopularityNames);
});

useIonViewDidLeave(()=>{
  setDisableButton(false);
  setComplexNames([]);
  setComplexNames([]);

  console.log(disableButton);
})

let adminId: string = "617fa1418aad8a43667f3651";
const [disableButton, setDisableButton] = useState<boolean>(false);
let past7Days: ResponseObject[];
let packPopularity: ResponseObject[];
let clientPopulation: ResponseObject[];

const [admin, setAdmin] = useState({
  adminId: "",
  name: "",
  surname: "",
  cellNum: "",
  password: ""
}); 

const [past7DaysValues, setPast7DaysValues] = useState<number[]>([]);
const [packPopularityNames, setPackPopularityNames] = useState<string[]>([]);
const [packPopularityValues, setPackPopularityValues] = useState<number[]>([]);
const [complexNames, setComplexNames] = useState<string[]>([]);
const [clients, setClients] = useState<number[]>([]);

const [showLoader, setShowLoader] = useState(true);
const [showError, setShowError] = useState(false);
const [errorMessage, setErrorMessage] = useState("");
const [callDone, setCallDone] = useState(false);

const getAdminDetails = (adminId: string) => {
  let url = 'get/'.concat(adminId);
  Connection.processGetRequest({}, url, (response: any)=>{
    mapAdminDetails(response);
  })
};

const getPast7Days = () => {
  let url = 'query/past7Days';
  Connection.processGetRequest({}, url, (response: any) =>{
     mapPast7Days(response);
  });
};

const getPopularity = () => {
  let url = 'query/packagePopularity';
  Connection.processGetRequest({}, url, (response: any) =>{
    mapPopularity(response);
 });
};

const getPopulation = () => {
  let url = 'query/clientPopulation';
  Connection.processGetRequest({}, url, (response: any) =>{
    mapPopulation(response);
 });
};
const mapPopulation = (response: any) => {
  if (response.type === "error") {
    setShowLoader(false);
    setErrorMessage(response.data);
    setShowError(true);
  } else {
    setShowLoader(false);
    clientPopulation = response.data.data;
    for (let i = 0; i < clientPopulation.length; i++) {
      complexNames.push(clientPopulation[i].name);
      clients.push(clientPopulation[i].value);
    }
  }
};

const mapPopularity = (response: any) => {
  if (response.type === "error") {
    setShowLoader(false);
    setErrorMessage(response.data);
    setShowError(true);
  } else {
    setShowLoader(false);
    packPopularity = response.data.data;
    for (let i = 0; i < packPopularity.length; i++) {
      packPopularityNames.push(packPopularity[i].name);
      packPopularityValues.push(packPopularity[i].value);
    }
  }
};

const mapAdminDetails = (response: any) => {
  if (response.type === "error") {
    setShowLoader(false);
    setErrorMessage(response.data);
    setShowError(true);
  } else {
    setShowLoader(false);
    setAdmin(response.data.data);
    setCallDone(true);
  }
};

const mapPast7Days = (response: any) => {
  if (response.type === "error") {
    setShowLoader(false);
    setErrorMessage(response.data);
    setShowError(true);
  } else {
    setShowLoader(false);
    past7Days = response.data.data;
    for (let i = 0; i < past7Days.length; i++) {
      past7DaysValues.concat(past7Days[i].value);
    }
  }
};

const state = {
  labels: ["Today", "Yesterday" , "2 Days Ago", "3 Days Ago", "4 Days Ago", "5 Days Ago", "6 Days Ago", "7 Days Ago"],
  datasets: [
    {
      label: "Bookings",
      backgroundColor: "rgba(91, 159, 78)",
      borderColor: "rgba(0,0,0,1)",
      borderWidth: 2,
      data: past7DaysValues,
    },
  ],
};
const state2 = {
  labels: packPopularityNames,
  datasets: [{
    label: 'Packages',
    data: packPopularityValues,
    backgroundColor: [
      'rgb(255, 99, 132)',
      'rgb(54, 162, 235)',
      'rgb(255, 205, 86)'
    ],
    hoverOffset: 4
  }]
};

const state3 = {
  labels: complexNames,
  datasets: [{
    label: 'Clients',
    data: clients,
    backgroundColor: [
      'rgb(255, 99, 132)',
      'rgb(54, 162, 235)',
      'rgb(255, 205, 86)'
    ],
    hoverOffset: 4
  }]
};

const removeUnit = () => {
  packPopularityNames.push("Default");
  let temp: string[] = [];
  for (let i = 0; i < packPopularityNames.length;i++) {
    if(packPopularityNames[i] !== "Default"){
      temp.push(packPopularityNames[i])
    }
    setPackPopularityNames(temp);
    setDisableButton(true);

  }
}

  return (
    <IonPage>
      <IonLoading
        cssClass="my-custom-class"
        showBackdrop
        isOpen={showLoader}
        message={"Please wait..."}
      />

      <IonAlert
        isOpen={showError}
        onDidDismiss={() => setShowError(false)}
        header={"Error"}
        subHeader={"Something went wrong."}
        message={errorMessage}
        buttons={["OK"]}
      />

      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>Dashboard</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen>
        <IonGrid>
          <IonRow>
            <IonCol>
                <IonTitle color="medium">{callDone === true ? "Welcome " + admin.name + " " + admin.surname + " !": ""}</IonTitle>
            </IonCol>
            <IonCol size="3">
                <IonButton size="small" type="reset" onClick={removeUnit} disabled={disableButton}>Show</IonButton>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonCardTitle color="medium" >Bookings for the past 7 days</IonCardTitle>
                  <IonCardContent>
                    {packPopularityNames.length !== 0 && packPopularityValues.length !== 0 && <Bar
                      data={state}
                    />}
                  </IonCardContent>
                </IonCardHeader>
              </IonCard>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonRow>
                    <IonCol size="8">
                      <IonCardTitle color="medium" >Package Popularity</IonCardTitle>
                    </IonCol>
                  </IonRow>
                  </IonCardHeader>
                  <IonCardContent>
                    {packPopularityNames.length !== 0 && packPopularityValues.length !== 0 && <Pie
                      data={state2}
                    />}
                  </IonCardContent>
              </IonCard>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonRow>
                    <IonCol size="8">
                      <IonCardTitle color="medium">Client Population</IonCardTitle>
                    </IonCol>
                  </IonRow>
                  </IonCardHeader>
                  <IonCardContent>
                    {packPopularityNames.length !== 0 && packPopularityValues.length !== 0 && <Doughnut
                    data={state3}
                    />}
                  </IonCardContent>
              </IonCard>
            </IonCol>
          </IonRow>
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Dashboard;
