import {
  IonButton,
  IonChip,
  IonCol,
  IonDatetime,
  IonGrid,
  IonIcon,
  IonInput,
  IonItem,
  IonItemDivider,
  IonItemGroup,
  IonLabel,
  IonRow,
} from "@ionic/react";
import { add, pin, remove } from "ionicons/icons";
import React, { ReactComponentElement, useState } from "react";
import Connection from "../../mixins/Connection";

const ComplexForm: React.FC<{
  name: string;
  isDisabled: boolean;
  content: any;
}> = (props) => {
  const {
    complexName,
    stree1,
    street2,
    city,
    province,
    postalCode,
    telephoneNum,
    startTime,
    endTime,
    cellNum,
    agents,
    units,
  } = props.content;

  const [unitList, setUnitList] = useState(['']);
  const [isUnit, setIsUnit] = useState(false);

  const updateUnitList = (unitList : any) => setUnitList(unitList);

  const addUnit = (e: any) => {
      if(e.key == 'Enter'){
          unitList.push(e.target.value);
          updateUnitList(unitList);
          console.log(unitList);
      }
  };

  const submitHandler = () => {
    if (props.name === "Add") {
      let url = "complex/add";

      // var payload = {
      //     complexName: comName,
      //     stree1: str1,
      //     street2: str2,
      //     city: cty,
      //     province: prov,
      //     postalCode: pos,
      //     telephoneNum: telNum,
      //     startTime: sTime,
      //     endTime: eTime,
      //     cellNum: cNum,
      //     agents: agents,
      //     units: un
      //   }
    } else if (props.name === "Update") {
      let url = "complex/update";
    } else {
      let url = "complex/remove/";
    }
  };

  return (
    <IonGrid>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Complex Name</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Street 1</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Street 2</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>City</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Province</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel>Postal code</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Telephone Number</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Operational Start Time</IonLabel>
            <IonDatetime displayFormat="HH:mm" />
          </IonItem>
        </IonCol>
        <IonCol>
          <IonItem>
            <IonLabel>Operational End Time</IonLabel>
            <IonDatetime displayFormat="HH:mm" />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Cell Number</IonLabel>
            <IonInput></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItemGroup>
            <IonItemDivider>
              <IonLabel>Units</IonLabel>
            </IonItemDivider>
            { unitList.map((unit)=>{
              <IonChip key={unit}>
                <IonIcon icon={pin} />
                <IonLabel>{unit}</IonLabel>
              </IonChip>})
            }
            <IonItem>
              <IonInput onKeyUp={e => addUnit(e)}/>
            </IonItem>
          </IonItemGroup>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton type="submit" expand="block" onClick={submitHandler}>
            {props.name}
          </IonButton>
        </IonCol>
      </IonRow>
    </IonGrid>
  );
};

export default ComplexForm;
