import {
  IonBackButton,
  IonButtons,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonInput,
  IonItem,
  IonLabel,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import React, { useState } from "react";
import { useParams } from "react-router";
import MultiForm from "../MultiForm/MultiForm";
import InputControls from "../InputControls/InputControls";

const CardDetails: React.FC = () => {
    const [selectedSegment, setSelectedSegment] = useState<"Update" | "Delete">("Update");
    const [isDisabled, setIsDisabled] = useState<true| false>(false);

  const { name, type, back } = useParams<{ name: string, type: "agent" | "complex" |"package" | "promotion", back: string}>();

  const selectedSegmentHandler = (selectedValue: "Update" | "Delete") => {
      setSelectedSegment(selectedValue);
      if(selectedValue == "Delete"){
        setIsDisabled(true);
      }else{
        setIsDisabled(false);
      }
  };

  const url = "/page/";

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton defaultHref={url.concat(back).concat("/").concat(type)}/>
          </IonButtons>
          <IonTitle>{name}</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
          <InputControls selectedValue={selectedSegment} onSelectedValue={selectedSegmentHandler}/>
          <MultiForm  buttonName={selectedSegment} type={type} isDisabled={isDisabled}/>
      </IonContent>
    </IonPage>
  );
};

export default CardDetails;
