/** @jsxImportSource theme-ui */
import {
  IonAlert,
  IonBackButton,
  IonButton,
  IonButtons,
  IonCard,
  IonCol,
  IonContent,
  IonFooter,
  IonGrid,
  IonHeader,
  IonImg,
  IonInput,
  IonItem,
  IonLabel,
  IonLoading,
  IonPage,
  IonRow,
  IonSelect,
  IonSelectOption,
  IonTitle,
  IonToast,
  IonToolbar,
} from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";
import TGWLogo from "../../components/TGWLogo.png";
import Connection from "../../mixins/Connection";
import { validCellNum, validEmail, validPassword } from "../../components/Regex/Regex";
import { Storage } from "@capacitor/storage";

const SignIn: React.FC = () => {
  const path = useHistory();

  let valid: boolean = false;
  let load: boolean = false;

  const [showLoader, setShowLoader] = useState(false);
  const [showError, setShowError] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const [cellNum, setCellNum] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [selection, setSelection] = useState("cell");

  const updateSelection = (selection: any) => {
    setSelection(selection);
    setEmail("");
    setCellNum("");
    setPassword("");
  };

  const updateCellNum = (cellNum: any) => {
    setCellNum(cellNum);
  };
  const updateEmail = (email: any) => {
    setEmail(email);
  };
  const updatePassword = (password: any) => {
    setPassword(password);
  };

  const setAdminId = async (data: any) => {
    await Storage.set({
      key: "adminId",
      value: data,
    });
  };

  const buttonHandler = () => {
    setShowLoader(true);
    if (selection === "cell") {
      if (cellNum === "" || password === "") {
        setShowLoader(false);
        valid = false;
        setErrorMessage("Fields cannot be empty.");
        setShowError(true);
      } else if (!validCellNum.test(cellNum)) {
        setShowLoader(false);
        valid = false;
        setErrorMessage("Invalid Cell Number.");
        setShowError(true);
      } else if (!validPassword.test(password)) {
        setShowLoader(false);
        valid = false;
        setErrorMessage(
          "Password must be 8-16 characters long. Ensure it includes atleast 1 aphabet character and 1 digit."
        );
        setShowError(true);
      } else {
        let url = "login/cell/".concat(cellNum, "/", password);
        Connection.processGetRequest({}, url, (response: any) =>
          mapResponse(response)
        );
      }
    } else {
      if (email === "" || password === "") {
        setShowLoader(false);
        valid = false;
        setErrorMessage("Fields cannot be empty.");
        setShowError(true);
      } else if (!validEmail.test(email)) {
        setShowLoader(false);
        valid = false;
        setErrorMessage("Invalid Email.");
        setShowError(true);
      } else if (!validPassword.test(password)) {
        setShowLoader(false);
        valid = false;
        setErrorMessage(
          "Password must be 8-16 characters long. Ensure it includes atleast 1 aphabet character and 1 digit."
        );
        setShowError(true);
      } else {
        let url = "login/email/".concat(email, "/", password);
        Connection.processGetRequest({}, url, (response: any) =>
          mapResponse(response)
        );
      }
    }
  };

  const mapResponse = async(response: any) => {
    if (response.type === "error") {
      setShowLoader(false);
      setErrorMessage(response.data);
      setShowError(true);
      setCellNum("");
      setEmail("");
      setPassword("");
    } else {
      setShowLoader(false);
      await setAdminId(response.data.data);
      setSuccessMessage("Successfully Sign In!");
      setShowSuccess(true);
      console.log(response.data.data)
    }
  };

  return (
    <IonPage>
      <IonContent>
        <IonImg src={TGWLogo} alt={"Logo.png"} sx={styles.img} />
        <IonToolbar>
          <IonLoading
            cssClass="my-custom-class"
            showBackdrop
            isOpen={showLoader}
            message={"Please wait..."}
          />

          <IonAlert
            isOpen={showError}
            onDidDismiss={() => setShowError(false)}
            header={"Error"}
            subHeader={"Something went wrong."}
            message={errorMessage}
            buttons={["OK"]}
          />

          <IonAlert
            isOpen={showSuccess}
            onDidDismiss={() => path.push("/dashboard")}
            header={"Success"}
            subHeader={successMessage}
            buttons={["OK"]}
          />
          <IonCard sx={{ mt: "8vh" }}>
            <IonGrid>
              <IonRow>
                <IonCol offset="4">
                  <IonToolbar>
                    <IonTitle size="large">Sign In</IonTitle>
                  </IonToolbar>
                </IonCol>
              </IonRow>
              <IonRow>
                <IonCol size="7">
                  {selection === "cell" && (
                    <IonItem>
                      <IonLabel position="floating">Cell Number</IonLabel>
                      <IonInput
                        value={cellNum}
                        onIonChange={(e) => updateCellNum(e.detail.value)}
                      ></IonInput>
                    </IonItem>
                  )}
                  {selection === "email" && (
                    <IonItem>
                      <IonLabel position="floating">Email</IonLabel>
                      <IonInput
                        value={email}
                        onIonChange={(e) => updateEmail(e.detail.value)}
                      ></IonInput>
                    </IonItem>
                  )}
                </IonCol>
                <IonCol size="5">
                  <IonSelect
                    sx={styles.select}
                    value={selection}
                    onIonChange={(e) => updateSelection(e.detail.value)}
                  >
                    <IonSelectOption value="cell">Cell Number</IonSelectOption>
                    <IonSelectOption value="email">Email</IonSelectOption>
                  </IonSelect>
                </IonCol>
              </IonRow>
              <IonRow>
                <IonCol>
                  <IonItem>
                    <IonLabel position="floating">Password</IonLabel>
                    <IonInput
                      type="password"
                      value={password}
                      onIonChange={(e) => updatePassword(e.detail.value)}
                    ></IonInput>
                  </IonItem>
                </IonCol>
              </IonRow>
              <IonRow>
                <IonCol>
                  <IonButton
                    shape="round"
                    type="submit"
                    expand="block"
                    onClick={() => buttonHandler()}
                  >
                    SignIn
                  </IonButton>
                </IonCol>
              </IonRow>
              <IonRow>
                <IonCol offset="4">
                  <p>
                    <a href="/forgot">Forgot password ?</a>
                  </p>
                </IonCol>
              </IonRow>
              <IonRow>
                <IonCol offset="2.5">
                  Don't have an account ? <a href="/signUp">Sign Up.</a>
                </IonCol>
              </IonRow>
            </IonGrid>
          </IonCard>
        </IonToolbar>
      </IonContent>
    </IonPage>
  );
};

export default SignIn;

const styles = {
  img: {
    pt: "6vh",
    pl: "4vh",
    pr: "4vh",
  },
  select: {
    mt: "15px",
    border: "1px solid grey",

  },
};
