import {
  IonBackButton,
  IonButtons,
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import React, { useState } from "react";
import { useParams } from "react-router";
import MultiForm from "../../components/MultiForm/MultiForm";
import InputControls from "../../components/InputControls/InputControls";

interface Agent {
  complexName: string;
  contractId: string;
  name: string;
  surname: string;
  id: string;
  cellNum: string;
  street1: string;
  street2: string;
  city: string;
  province: string;
  postalCode: string;
  password: string;
}

interface Complex {
  name: string;
  street1: string;
  street2: string;
  city: string;
  province: string;
  postalCode: string;
  telephoneNum: string;
  startTime: string;
  endTime: string;
  cellNum: string;
  units: [];
  agents: [];
}

interface Package {
  packageId: string;
  promotionId: string;
  packageName: string;
  minutes: number;
  standardPrice: string;
  suvPrice: string;
  description: string;
  onPromotion: boolean;
}

interface Promotion {
  promotionId: string;
  packageName: string;
  standardPrice: string;
  suvPrice: string;
  isEnabled: boolean;
}

const CardDetails: React.FC = () => {
  const [selectedSegment, setSelectedSegment] = useState<"Update" | "Delete">(
    "Update"
  );
  const [isDisabled, setIsDisabled] = useState<true | false>(false);

  const { name, type, back } = useParams<{
    name: string;
    type: "agent" | "complex" | "package" | "promotion";
    back: string;
  }>();

  const selectedSegmentHandler = (selectedValue: "Update" | "Delete") => {
    setSelectedSegment(selectedValue);
    if (selectedValue == "Delete") {
      setIsDisabled(true);
    } else {
      setIsDisabled(false);
    }
  };

  let result;

  const url = "/page/";
  /*Assign Content from API*/
  if (type === "agent") {
    const agent: Agent = {
      complexName: "",
      contractId: "",
      name: "",
      surname: "",
      id: "",
      cellNum: "",
      street1: "",
      street2: "",
      city: "",
      province: "",
      postalCode: "",
      password: "",
    };

    result = agent;
  } else if (type === "complex") {
    const complex: Complex = {
      name: "",
      street1: "",
      street2: "",
      city: "",
      province: "",
      postalCode: "",
      telephoneNum: "",
      startTime: "",
      endTime: "",
      cellNum: "",
      units: [],
      agents: [],
    };

    result = complex;
  } else if (type === "package") {
    const pack: Package = {
      packageId: "",
      promotionId: "",
      packageName: "",
      minutes: 0,
      standardPrice: "",
      suvPrice: "",
      description: "",
      onPromotion: false,
    };

    result = pack;
  } else {
    const promotion: Promotion = {
      promotionId: "",
      packageName: "",
      standardPrice: "",
      suvPrice: "",
      isEnabled: false,
    };

    result = promotion;
  }

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton
              defaultHref={url.concat(back).concat("/").concat(type)}
            />
          </IonButtons>
          <IonTitle>{name}</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <InputControls
          selectedValue={selectedSegment}
          onSelectedValue={selectedSegmentHandler}
        />
        <MultiForm
          buttonName={selectedSegment}
          type={type}
          isDisabled={isDisabled}
          content={result}
        />
      </IonContent>
    </IonPage>
  );
};

export default CardDetails;
