import {
  IonAlert,
  IonButton,
  IonCol,
  IonGrid,
  IonInput,
  IonItem,
  IonLabel,
  IonLoading,
  IonRow,
  IonToggle,
} from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";
import Connection from "../../mixins/Connection";

const PromotionForm: React.FC<{
  name: string;
  isDisabled: boolean;
  content: any;
}> = (props) => {
  var {
    promotionId,
    promotionName,
    packageName,
    standardPrice,
    suvPrice,
    isEnabled,
  } = props.content;

  const [ showLoader, setShowLoader ] = useState(false);
  const [ showSuccess, setShowSuccess ] = useState(false);
  const [ showError, setShowError ] = useState(false);
  const [ errorMessage, setErrorMessage ] = useState('');

  const [enteredPromotionName, setPromotionName] = useState(promotionName);
  const [enteredPackageName, setPackageName] = useState(packageName);
  const [enteredStandardPrice, setStandardPriceName] = useState(standardPrice);
  const [enteredSuvPrice, setSuvPrice] = useState(suvPrice);
  const [enteredIsEnabled, setIsEnabled] = useState(isEnabled);

  const updatePromotionName = (promotionName: any) =>
    setPromotionName(enteredPromotionName);
  const updatePackageName = (packageName: any) =>
    setPackageName(enteredPackageName);
  const updateStandardPrice = (standardPrice: any) =>
    setStandardPriceName(enteredStandardPrice);
  const updateSuvPrice = (suvPrice: any) => setSuvPrice(enteredSuvPrice);
  const updateIsEnabled = (isEnabled: any) => setIsEnabled(enteredIsEnabled);

  const path = useHistory();

  const buttonHandler = () => {
    if (props.name === "Update") {
      let url = "promotion/update";
      var payload = {
        promotionId: promotionId,
        packageName: enteredPackageName,
        standardPrice: enteredStandardPrice,
        suvPrice: enteredSuvPrice,
        isEnabled: enteredIsEnabled,
      };

      Connection.processPostRequest(payload, url, (response: any) => {
        mapUpdateResponse(response);
      });
    } else if (props.name === "Delete") {
      let url = "promotion/remove/".concat(promotionId);

      Connection.processDeleteRequest({}, url, (response: any) => {
        mapDeleteResponse(response);
      });
    } else if (props.name === "Add") {
      let url = "promotion/add";
      var payload = {
        promotionId: promotionId,
        packageName: enteredPackageName,
        standardPrice: enteredStandardPrice,
        suvPrice: enteredSuvPrice,
        isEnabled: enteredIsEnabled,
      };
      Connection.processPostRequest(payload, url, (response: any) => {
        mapAddResponse(response);
      });
    }
  };

  const mapUpdateResponse = (response: any) => {
    if (response.type === "error") {
      setErrorMessage(response.data)
      setShowError(true);
    } else {
      setShowSuccess(true);
    }
  };
  const mapDeleteResponse = (response: any) => {
    if (response.type === "error") {
      console.log("error!");
    } else {
      console.log("Saved!");
    }
  };
  const mapAddResponse = (response: any) => {
    if (response.type === "error") {
      console.log("error!");
    } else {
      console.log("Saved!");
    }
  };

  return (
    <IonGrid>
      <IonLoading
          cssClass='my-custom-class'
          showBackdrop
          isOpen={showLoader}
          message={'Please wait...'}
        />

        <IonAlert
          isOpen={showError}
          onDidDismiss={() => setShowError(false)}
          header={'Error'}
          subHeader={'Something went wrong.'}
          message={errorMessage}
          buttons={['OK']}
        />

        <IonAlert
          isOpen={showSuccess}
          onDidDismiss={() => path.replace("/page/Promotion/promotion")}
          header={'Success'}
          subHeader={'Account details updated.'}
          buttons={['OK']}
        />
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Promotion Name</IonLabel>
            <IonInput
              disabled={props.isDisabled}
              value={promotionName}
              onIonChange={(e) => updatePromotionName(e.detail.value)}
            ></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Package Name</IonLabel>
            <IonInput
              disabled={props.isDisabled}
              value={enteredPackageName}
              onIonChange={(e) => updatePackageName(e.detail.value)}
            ></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Standard Price</IonLabel>
            <IonInput
              disabled={props.isDisabled}
              value={enteredStandardPrice}
              onIonChange={(e) => updateStandardPrice(e.detail.value)}
            ></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Suv Price</IonLabel>
            <IonInput
              disabled={props.isDisabled}
              value={enteredSuvPrice}
              onIonChange={(e) => updateSuvPrice(e.detail.value)}
            ></IonInput>
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel>Enabled</IonLabel>
            <IonToggle
              checked={enteredIsEnabled}
              disabled={props.isDisabled}
              onIonChange={(e) => updateIsEnabled(e.detail.value)}
            />
          </IonItem>
        </IonCol>
      </IonRow>
      <IonRow>
        <IonCol>
          <IonButton type="submit" expand="block" onClick={buttonHandler}>
            {props.name}
          </IonButton>
        </IonCol>
      </IonRow>
    </IonGrid>
  );
};

export default PromotionForm;
