import {
  IonAlert,
  IonButtons,
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardTitle,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonLoading,
  IonMenuButton,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
  useIonViewDidEnter,
  useIonViewWillEnter,
} from "@ionic/react";
import { stringify } from "querystring";
import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import Connection from "../../mixins/Connection";

interface ResponseObject{
  name: string,
  value: number
}
interface Admin {
  adminId: string;
  name: string;
  surname: string;
  cellNum: string;
  password: string;
}

const Dashboard: React.FC = () => {

useIonViewDidEnter(()=>{
  setShowLoader(true);
  getAdminDetails(adminId);
  getPast7Days();
});
let adminId: string = "617fa1418aad8a43667f3651";
let past7Days: ResponseObject[];
let admin: Admin ={
  adminId: "",
  name: "Jackson",
  surname: "Mthembu",
  cellNum: "",
  password: ""
}; 
var past7DaysValues: number[] = new Array;

const [showLoader, setShowLoader] = useState(true);
const [showError, setShowError] = useState(false);
const [errorMessage, setErrorMessage] = useState("");
const [callDone, setCallDone] = useState(false);

const getAdminDetails = (adminId: string) => {
  let url = '/get/'.concat(adminId);
  Connection.processGetRequest({}, url, (response: any)=>{
    mapAdminDetails(response, admin);
  })
};

const getPast7Days = () => {
  let url = '/query/past7Days'
  Connection.processGetRequest({}, url, (response: any) =>{
     mapPast7Days(response);
  })
};

const mapAdminDetails = (response: any, admin: Admin) => {
  if (response.type === "error") {
    setShowLoader(false);
    setErrorMessage(response.data);
    setShowError(true);
  } else {
    setShowLoader(false);
    admin = response.data.data;
    console.log(admin);
    setCallDone(true);
  }
};

const mapPast7Days = (response: any) => {
  if (response.type === "error") {
    setShowLoader(false);
    setErrorMessage(response.data);
    setShowError(true);
  } else {
    setShowLoader(false);
    past7Days = response.data.data;
    for (let i = 0; i < past7Days.length; i++) {
      past7DaysValues.push(past7Days[i].value);
    }
    past7DaysValues.push(75);
  }
};

const state = {
  labels: ["Today", "Yesterday" , "2 Days Ago", "3 Days Ago", "4 Days Ago", "5 Days Ago", "6 Days Ago", "7 Days Ago"],
  datasets: [
    {
      label: "Bookings",
      backgroundColor: "rgba(75,192,192,1)",
      borderColor: "rgba(0,0,0,1)",
      borderWidth: 2,
      data: past7DaysValues,
    },
  ],
};



  return (
    <IonPage>
      <IonLoading
        cssClass="my-custom-class"
        showBackdrop
        isOpen={showLoader}
        message={"Please wait..."}
      />

      <IonAlert
        isOpen={showError}
        onDidDismiss={() => setShowError(false)}
        header={"Error"}
        subHeader={"Something went wrong."}
        message={errorMessage}
        buttons={["OK"]}
      />

      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>Dashboard</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen>
        <IonGrid>
          <IonRow>
            <IonCol>
              <IonTitle color="grey">{callDone === true? "Welcome " + admin.name + " " + admin.surname + " !": ""}</IonTitle>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
              <IonCard>
                <IonCardHeader>
                  <IonCardTitle color="grey" >Bookings for the past 7 days</IonCardTitle>
                  <IonCardContent>
                    <Bar
                      data={state}
                    />
                  </IonCardContent>
                </IonCardHeader>
              </IonCard>
            </IonCol>
          </IonRow>
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Dashboard;
