/** @jsxImportSource theme-ui */
import { IonAlert, IonBackButton, IonButton, IonButtons, IonCard, IonCardContent, IonCardHeader, IonCardSubtitle, IonContent, IonHeader, IonInput, IonItem, IonLabel, IonPage, IonTitle, IonToolbar, useIonViewWillEnter } from "@ionic/react";
import React, { useState } from "react";
import { useHistory } from "react-router";

const NewPassword: React.FC = () => {
const path = useHistory();

    const [message, setMessage] = useState("");
    const[showInfo, setShowInfo] = useState(false);
  const [showError, setShowError] = useState(false);
  const [showLoader, setShowLoader] = useState(false);

useIonViewWillEnter(() => {
    document.addEventListener('backbutton', (ev: any) => {
        setMessage("Are you sure ? Going back will undo all of your progress and your OTP will be liquidated");
        setShowInfo(true);
    });
});

const [password, setPassword] = useState("");
const [cPassword, setCPassword] = useState("");

const updatePassword = (password: any) => setPassword(password.target.value);
const updateCPassword = (cPassword: any) => setCPassword(cPassword.target.value);

const buttonHandler = () => {
    if(password === '' && cPassword === ''){
        setShowLoader(false);
        setMessage('Fields must not be empty.');
        setShowError(true);
    }
};

  return (
    <IonPage>
        <IonAlert
        isOpen={showError}
        onDidDismiss={() => setShowError(false)}
        header={"Error"}
        subHeader={"Something went wrong."}
        message={message}
        buttons={["OK"]}
      />
        <IonAlert
          isOpen={showInfo}
          onDidDismiss={() => setShowInfo(false)}
          cssClass='my-custom-class'
          header={'Warning'}
          message={message}
          buttons={[
            {
              text: 'Cancel',
              role: 'cancel',
              cssClass: 'secondary',
              handler: () => {

              }
            },
            {
              text: "Yes, I'm sure",
              handler: () => {
                path.replace('/signIn')
              }
            }
          ]}
        />
      <IonHeader>
        <IonToolbar color="primary">
          <IonTitle>New Password </IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
          <IonCard sx={styles.card}>
              <IonCardHeader>
                  <IonTitle size='small' sx={styles.card.title}> Please enter your new Password below:</IonTitle>
              </IonCardHeader>
              <IonCardContent>
                  <IonItem>
                      <IonLabel position="floating">Password</IonLabel>
                      <IonInput type="password" onIonChange={(e) => updatePassword(e)}/>
                  </IonItem>
                  <IonItem sx={styles.input}>
                      <IonLabel position="floating"> Confirm Password</IonLabel>
                      <IonInput type="password" onIonChange={(e) => updateCPassword(e)}/>
                  </IonItem>
                  <IonButton type='submit' expand='block' shape='round' onClick={buttonHandler}>Submit</IonButton>
              </IonCardContent>
          </IonCard>
      </IonContent>
    </IonPage>
  );
};

export default NewPassword;

const styles = {
    input: {
        mb: 4,
    },

    card: {
        title:{
            pl: 4,
        },
        mt: '25vh',
    },
};
